int main(int argc, char **argv)
{
  int width=6;
  int height=12;
  printf("#include \"font.h\"\n");
  printf("char font[96][%d]={\n", height);
  for (int c = 0; c < 96; c++) {
    int xoff = (c%32)*width;
    int yoff = (c/32)*height;
    printf("{");
    for (int y = 0; y < height; y++) {
      int scanline = 0;
      for (int x = width-1; x >= 0; x--) {
        scanline <<= 1;
        scanline |= font[y+yoff][x+xoff] == '#';
      }
      if (y) printf(",");
      printf("0x%02x", scanline);
    }
    printf("}, // ascii %d = '%c'\n", c+32, c+32);
  }
  printf("};\n");
  return 0;
}
